mapper.select:
SELECT questionFileNo, questionNo, savedFileName, userFileName, downloadCount 
FROM questionfile
WHERE questionNo = #{ questionNo }

SELECT questionFileNo, questionNo, savedFileName, userFileName, downloadCount 
FROM questionfile
WHERE questionFILENO = #{ resumeFileNo }

mapper.insert:
INSERT INTO questionfile (
	questionNO
	, savedFileName
	, userFileName
)
VALUES (

	#{ questionNo }
	, #{ savedFileName }
	, #{ userFileName }
)

mapper.update:
UPDATE question
SET DELETED = '1'
WHERE questionNo = #{ questionNo }

UPDATE question
SET READCOUNT = READCOUNT+1
WHERE questionNo = #{ questionNo }

UPDATE question
SET CATEGORY =  #{ category }, title = #{ title }, content = #{ content }
WHERE questionNo = #{ questionNo }

UPDATE questioncomment
SET CONTENT = #{ content }
WHERE COMMENTNO = #{ commentNo }

mapper.select:
SELECT COMMENTNO, QUESTIONNO, WRITER, CONTENT, REGDATE, GROUPNO, STEP, DEPTH 
FROM questioncomment
WHERE commentno = #{ questionNo }

mapper.update:
UPDATE questioncomment
SET STEP = STEP + 1
WHERE GROUPNO = #{ groupNo } AND STEP > #{ step }

mapper.select:
SELECT questionNo, category, title, uploader, regDate FROM question WHERE deleted = '0'
AND category = #{category}
ORDER BY questionNo DESC
